void laser_init() {

	laser_top_dist(0);
	laser_left_dist(0);
	laser_right_dist(0);

}

void laser_draw() {

	// Top Laser

	draw3D_box(LASER_TOP_X,LASER_TOP_Y,LASER_TOP_Z,LASER_TOP_L,LASER_TOP_H,LASER_TOP_D,LASER_COLOR);

	// Left Laser

	draw3D_box(LASER_LEFT_X,LASER_LEFT_Y,LASER_LEFT_Z,LASER_LEFT_L,LASER_LEFT_H,LASER_LEFT_D,LASER_COLOR);

	// Right Laser

	draw3D_box(LASER_RIGHT_X,LASER_RIGHT_Y,LASER_RIGHT_Z,LASER_RIGHT_L,LASER_RIGHT_H,LASER_RIGHT_D,LASER_COLOR);
}

void laser_top_dist(int dist) {

	if(dist > LASER_TOP_MAX || dist < 0) {

		dist = LASER_TOP_MAX;

	}

	laser_top_ey = LASER_TOP_SY - dist;
	laser_top = dist;
	laser_top_mm = LASER_TOP_MAX - dist;
}

void laser_left_dist(int dist) {

	if(dist > LASER_LEFT_MAX || dist < 0) {

		dist = LASER_LEFT_MAX;

	}

	laser_left_ez = LASER_LEFT_SZ + dist;
	laser_left = dist;
	laser_left_mm = LASER_LEFT_MAX - dist;

}

void laser_right_dist(int dist) {

	if(dist > LASER_RIGHT_MAX || dist < 0) {

		dist = LASER_RIGHT_MAX;

	}

	laser_right_ez = LASER_RIGHT_SZ - dist;
	laser_right = dist;
	laser_right_mm = LASER_RIGHT_MAX - dist;

}


void laser_drawLaserShots() {

	draw3D_line(LASER_TOP_X,LASER_TOP_SY,LASER_TOP_Z,LASER_TOP_X,laser_top_ey,LASER_TOP_Z,LASER_SHOT_COLOR);

	draw3D_circle(LASER_TOP_X,laser_top_ey,LASER_TOP_Z,LASER_IMPACT_RADIUS,LASER_IMPACT_COLOR);

	draw3D_line(LASER_LEFT_X,LASER_LEFT_Y,LASER_LEFT_SZ,LASER_LEFT_X,LASER_LEFT_Y,laser_left_ez,LASER_SHOT_COLOR);

	draw3D_circle(LASER_LEFT_X,LASER_LEFT_Y,laser_left_ez,LASER_IMPACT_RADIUS,LASER_IMPACT_COLOR);

	draw3D_line(LASER_RIGHT_X,LASER_RIGHT_Y,LASER_RIGHT_SZ,LASER_RIGHT_X,LASER_RIGHT_Y,laser_right_ez,LASER_SHOT_COLOR);

	draw3D_circle(LASER_RIGHT_X,LASER_RIGHT_Y,laser_right_ez,LASER_IMPACT_RADIUS,LASER_IMPACT_COLOR);

}

void laser_top_move(int mm) {

	laser_top_dist(laser_top + mm);

}

void laser_left_move(int mm) {

	laser_left_dist(laser_left + mm);

}

void laser_right_move(int mm) {

	laser_right_dist(laser_right + mm);

}

void laser_run() {

	laser_x = conveyorMoved - LASER_START_X;



	int top = analRead(1);
	int left = analRead(0);
	int right = analRead(2);

	//laser_top_dist(laserDistances_v1[1023-top]);
	//laser_left_dist(laserDistances_v1[1023-left]);
	//laser_right_dist(laserDistances_v1[1023-right]);

	laserTopAvg[systemCounter%LASER_AVG] = laserDistances_v1[1023-top]+LASER_TOP_OFFSET;
	laserLeftAvg[systemCounter%LASER_AVG] = laserDistances_v1[1023-left];
	laserRightAvg[systemCounter%LASER_AVG] = laserDistances_v1[1023-right];

	int i;

	top = 0;
	left = 0;
	right = 0;

	for(i = 0; i < LASER_AVG; i++) {

		top += laserTopAvg[i];
		left += laserLeftAvg[i];
		right += laserRightAvg[i];
	}

	top = top/LASER_AVG;
	left = left/LASER_AVG;
	right = right/LASER_AVG;

	laser_top_dist(top);
	laser_left_dist(left);
	laser_right_dist(right);

	// Detecting Wood

	if(autoMode) {
		if(laser_top_mm > 5) woodDetected = 1;
		else woodDetected = 0;
	}

	wood_detect();

}

